import{_ as e,X as c,Y as d,a1 as l}from"./framework-ca96282b.js";const r={},o=l('<h1 id="firewalld防火墙基本命令" tabindex="-1"><a class="header-anchor" href="#firewalld防火墙基本命令" aria-hidden="true">#</a> Firewalld防火墙基本命令</h1><p>适用于centos7</p><p>启动： <code>systemctl start firewalld</code><br> 关闭： <code>systemctl stop firewalld</code><br> 查看状态： <code>systemctl status firewalld</code><br> 开机禁用 ： <code>systemctl disable firewalld</code><br> 开机启用 ： <code>systemctl enable firewalld</code><br> 启动一个服务：<code>systemctl start firewalld.service</code><br> 关闭一个服务：<code>systemctl stop firewalld.service</code><br> 重启一个服务：<code>systemctl restart firewalld.service</code><br> 显示一个服务的状态：<code>systemctl status firewalld.service</code><br> 在开机时启用一个服务：<code>systemctl enable firewalld.service</code><br> 在开机时禁用一个服务：<code>systemctl disable firewalld.service</code><br> 查看服务是否开机启动：<code>systemctl is-enabled firewalld.service</code><br> 查看已启动的服务列表：<code>systemctl list-unit-files|grep enabled</code><br> 查看启动失败的服务列表：<code>systemctl --failed</code><br> 查看版本： <code>firewall-cmd --version</code><br> 查看帮助： <code>firewall-cmd --help</code><br> 显示状态： <code>firewall-cmd --state</code><br> 查看所有打开的端口： <code>firewall-cmd --zone=public --list-ports</code><br> 更新防火墙规则： <code>firewall-cmd --reload</code><br> 查看区域信息: <code>firewall-cmd --get-active-zones</code><br> 查看指定接口所属区域： <code>firewall-cmd --get-zone-of-interface=eth0</code><br> 拒绝所有包：<code>firewall-cmd --panic-on</code><br> 取消拒绝状态： <code>firewall-cmd --panic-off</code><br> 查看是否拒绝： <code>firewall-cmd --query-panic</code><br> 添加端口<code>firewall-cmd --zone=public --add-port=80/tcp --permanent</code> （--permanent永久生效，没有此参数重启后失效）<br> 重新载入<code>firewall-cmd --reload</code><br> 查看<code>firewall-cmd --zone= public --query-port=80/tcp</code><br> 删除<code>firewall-cmd --zone= public --remove-port=80/tcp --permanent</code></p>',3),t=[o];function a(s,i){return c(),d("div",null,t)}const n=e(r,[["render",a],["__file","Firewalld防火墙基本命令.html.vue"]]);export{n as default};
